import {useState, useEffect} from "react";

import MovieCard from "./MovieCard";

//b7b346f7

import './App.css'
import SearchIcon from './search.svg'


const API_URL = 'http://www.omdbapi.com?apikey=b7b346f7';

const movie1 = {
    "Title": "Fighting, Flying and Driving: The Stunts of Spiderman 3",
    "Year": "2007",
    "imdbID": "tt1132238",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BNTI3NDE1ZmEtMTRiMS00YTY4LTk0OGItNjY4YmI0MDM4OGM4XkEyXkFqcGdeQXVyODE2NDgwMzM@._V1_SX300.jpg"
}

const App = ()=>{
    const [movies, setMovies] = useState([]);

    const searchMovies = async (title)=>{
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();
        setMovies(data.Search)
    }
    useEffect(()=>{
        searchMovies('Spiderman')
    },[])

    return(
        <div className="app">
            <h1>Shamsher's Collection</h1>
            <div className="search">
                <input placeholder="Search for movies"
                value = "Superman"
                onChange={()=>{      
                }}
                />
                <img src={SearchIcon} alt ="Search"
                    onClick={()=>{}}
                />
            </div>
                {
                    movies?.length>0
                    ? (
                        <div className="container">
                           {
                            movies.map((movie)=>(
                                <MovieCard movie = {movie} />
                            ))
                           }
                        </div>
                      ) : (
                        <div className="empty">
                            <h2>No movies found</h2>
                        </div>
                      )
                }

            <div className="container">
               <MovieCard movie1={movies1} />
            </div>
        </div>

    );
}
export default App;
